   RasterCodecs codecs = new RasterCodecs();

   geoKeySrcStream = inStreamGeyKey;
   geoKeyDestStream = outStreamGeyKey;

   codecs.GeoKeyFound += new EventHandler<CodecsEnumGeoKeysEventArgs>(codecs_MyGeyKeyFound);
