public void DigitalSubtractConstructorExample(RasterImage image, RasterImage maskImage, Stream outStream)
{
   DigitalSubtractCommand command = new DigitalSubtractCommand(maskImage, DigitalSubtractCommandFlags.ContrastEnhancement);
   command.Run(image);
   // Save result image
   RasterCodecs codecs = new RasterCodecs();
   codecs.Save(image, outStream, RasterImageFormat.Jpeg, 24);
   image.Dispose();
   maskImage.Dispose();
}

