public void MedicalViewerCellCollectionExample()
{
   int index;
   MedicalViewerForm myForm = GetMedicalControl();
   MedicalViewer medicalViewer = myForm.Viewer;
   medicalViewer.Cells.SelectAll(false);
   for (index = 0; index < medicalViewer.Cells.Count; index++)
      if ((index % 2) == 0)
         medicalViewer.Cells[index].Selected = true;
   medicalViewer.Cells.FreezeSelected(true);
   myForm.ShowDialog();
}

public void MedicalViewerCellCollectionInvertSelectionExample()
{
   int index;
   MedicalViewerForm myForm = GetMedicalControl();
   MedicalViewer medicalViewer = myForm.Viewer;

   medicalViewer.Cells.FreezeAll(false);
   for (index = 0; index < medicalViewer.Cells.Count; index++)
      if ((index % 2) == 0)
         medicalViewer.Cells[index].Selected = true;

   medicalViewer.Cells.InvertSelection();
   medicalViewer.Cells.FreezeSelected(true);
   myForm.ShowDialog();
}

public void MedicalViewerCellCollectionRepositionCellsExample()
{
   MedicalViewerForm myForm = GetMedicalControl();
   MedicalViewer medicalViewer = myForm.Viewer;
   medicalViewer.Cells.Reposition(0, 1, true);

   myForm.ShowDialog();
}

