   byte b = (byte)rand.Next(256);

   RasterColor clr = new RasterColor(a, r, g, b);

   // Check
   Debug.Assert(clr.A == a);
   Debug.Assert(clr.R == r);
   Debug.Assert(clr.G == g);
   Debug.Assert(clr.B == b);
}

